html, body {
  margin: 0;
  padding: 0;
}
canvas {
  display: block;
}

class Stars {

  constructor(x, y, v) {
    this.x = x;
    this.y = y;
    this.v = v;
  }

  show() {
    colorMode(RGB);
    stroke(255, 30);
    fill(255);
    strokeWeight(10);
    this.d = random(this.v + 4);
    circle(this.x, this.y, this.d);
  }

  move() {
    this.y = this.y + this.v;
    if (this.y > height) {
      this.x = random(0, width);
      this.y = 0;
    }
  }
}

class Rocket {

  show() {
    // fire outer
    colorMode(RGB);
    var fireOuter = 135 + random(0, 5);
    noStroke();
    fill("#ebc634");
    beginShape();
    curveVertex(0, 90)
    curveVertex(12, 110);
    curveVertex(0, fireOuter);
    curveVertex(0, fireOuter);
    curveVertex(-12, 110);
    curveVertex(0, 90);
    endShape();

    // fire inner
    var fireInner = 122 + random(0, 3);
    noStroke();
    fill("#e05928");
    beginShape();
    curveVertex(0, 90)
    curveVertex(8, 110);
    curveVertex(0, fireInner);
    curveVertex(0, fireInner);
    curveVertex(-8, 110);
    curveVertex(0, 90);
    endShape();


    // rocket bottom
    stroke(80);
    strokeWeight(2);
    fill(110);
    beginShape();
    vertex(12, 100);
    vertex(12, 105);
    vertex(-12, 105);
    vertex(-12, 100);
    endShape(CLOSE);


    // rocket mainbody
    stroke(160);
    strokeWeight(2);
    fill(170);
    beginShape();
    vertex(0, 55);
    vertex(25, 75);
    vertex(30, 105);
    vertex(0, 90);
    vertex(-30, 105);
    vertex(-25, 75)
    endShape(CLOSE)

    stroke(190);
    strokeWeight(3);
    fill(210);
    beginShape();
    vertex(0, 0);
    bezierVertex(15, 8, 20, 20, 20, 40);
    vertex(20, 50);
    vertex(16, 100);
    vertex(-16, 100);
    vertex(-20, 50);
    vertex(-20, 40);
    bezierVertex(-20, 20, -15, 8, 0, 0);
    endShape();

    stroke(160);
    strokeWeight(4);
    line(0, 70, 0, 105);

    stroke(100, 150, 180);
    fill(150, 200, 230);
    circle(0, 45, 20);
  }
}

class Bullets {

  constructor(x, y) {
    this.x = x;
    this.y = y;
  }

  shot() {
    stroke('#42e5ff');
    strokeWeight(3);
    noFill;
    line(this.x, this.y - 15, this.x, this.y);
    this.y -= 5;
  }
}

