//Method 1: More flexible, any order
function nextPage() {
  if (mode == 0) {
    mode = 1;
    //page 2
    redButt.show();
    orangeButt.show();
  } else if (mode == 1) {
    mode = 2;
    //page 3
    redButt.hide();
    orangeButt.hide();
  } else if (mode == 2) {
    mode = 0;
    //page 1
  }
}

