function heartbeat(x, y) {
  imageMode(CORNER);
   if(d<=250){
      scale0=map(d,0,250,0,130);
  } else{
      scale0=130;
  }

  push();
  rotate(d * 0.005);
  fill(77, 11, 238, 100);
  drawingContext.shadowBlur = 40;
  drawingContext.shadowColor = color(0, 102, 255,80);
  polarTriangle(0, scale0, 0);
  rotate(d * 0.005);
  fill(77, 11, 83, 100);
  polarTriangle(180, scale0, 0);
  pop();

   push();
