function redPressed() {
  let pitch;
  pitch="A1";
  beginShape();
  fill(255,0,0)
  vertex(330,180)
  vertex(250,180)
  vertex(220,95)
  vertex(180,180)
  vertex(100,180)
  vertex(165,235)
  vertex(140,305)
  vertex(215,265)
  vertex(290,305)
  vertex(265,235)
  endShape(CLOSE);

  if(pitch && sampler.loaded){
    // Sampler will repitch the closest sample
  	sampler.triggerAttack(pitch);
  }
}

